! function (t) {
    t(".btn-audio-caption").click(function (i) {
        t(this).closest(".audio").toggleClass("caption-is-active")
    }), t(".btn-audio-video").click(function (i) {
        t(this).closest(".audio").toggleClass("audio-video-is-active")
    }), t(".btn-start").click(function (i) {
        t("html, body").animate({
            scrollTop: t("#main").offset().top
        }, 1e3)
    })
}(jQuery);